import React from "react";
import { BrowserRouter as Router, Routes, Route } from "react-router-dom";
import CategoryForm from "./components/CategoryForm"; // Import the new CategoryForm component

const App = () => {
  return (
    <Router>
      <div>
        <header className="app-header">
          <h1 className="text-center text-3xl font-bold my-4">Category Form Application</h1>
        </header>
        <main>
          <Routes>
            {/* Route for Category Form */}
            <Route path="/category" element={<CategoryForm />} />

            {/* Default Route */}
            <Route
              path="*"
              element={
                <div className="text-center mt-8">
                  <p className="text-xl">Welcome! Use the link below to access the form.</p>
                  <nav className="mt-4">
                    <a
                      href="/category"
                      className="mx-4 py-2 px-4 bg-green-600 text-white rounded hover:bg-green-700"
                    >
                      Go to Category Form
                    </a>
                  </nav>
                </div>
              }
            />
          </Routes>
        </main>
        <footer className="text-center py-4 mt-8 border-t">
          <p className="text-gray-600">&copy; 2024 Category Form Application</p>
        </footer>
      </div>
    </Router>
  );
};

export default App;
